
package lgcns;


import java.io.IOException;
import java.math.BigInteger;

import org.web3j.crypto.CipherException;
import org.web3j.crypto.Credentials;
import org.web3j.crypto.WalletUtils;
import org.web3j.protocol.Web3j;
import org.web3j.protocol.http.HttpService;

import Srv.App;

public class ContractRequestor extends App {

    public ContractRequestor() {
        // TODO Auto-generated constructor stub
    }

    @Override
    public void doSvc() {
        String ipfs = (String) data.get("ipfs");
        String hash = (String) data.get("hash");
        String createDate = (String) data.get("createDate");
        
        StringBuffer log = (StringBuffer) data.get("log");
        
        try {
            // TODO Auto-generated method stub
            String addr = TSAServlet.etherUrl;
            String BINARY = "";
            Web3j web3 = Web3j.build(new HttpService(addr)); // defaults to http://localhost:8545/
            Credentials credentials;
            credentials = WalletUtils.loadCredentials(TSAServlet.walletPw,TSAServlet.walletPh );
            // TODO Auto-generated catch block
            // TransactionReceipt transactionReceipt = Transfer.sendFunds(web3, credentials, "0x1054E5F19863C184319bCbA7c739AabaE66388eE", BigDecimal.valueOf(20.0), Convert.Unit.ETHER).send();
            String[] _paramList = { ipfs, hash, createDate };
            TsaContract result;
            result = TsaContract.deploy(web3, credentials, BigInteger.valueOf(1000000), BigInteger.valueOf(1000000), _paramList).send();
            String contractAddress = result.getContractAddress();
            System.out.println("林家:" + contractAddress +"\n");
            log.append("拌距林家 : "+contractAddress);
            data.put("contract", contractAddress);
            
            
        } catch (Exception e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }

    @Override
    public boolean stop() {
        // TODO Auto-generated method stub
        return false;
    }
}
